import React, { useState, useRef, useEffect } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import 'gridstack/dist/gridstack.min.css';
import './App.css';
import './litegride.css';
import { GridStack } from 'gridstack';
import Tema from './assets/img/tema.jpg';

function App() {

  //const chartContainerRef = useRef(null);



  useEffect(() => {
    GridStack.init();

    // const handleResize = () => {
    //   Highcharts.charts.forEach(chart => {
    //     if (chart && chart.renderTo === chartContainerRef.current) {
    //       chart.reflow();
    //     }
    //   });
    // };

    // handleResize();

    // window.addEventListener('resize', handleResize);

    // return () => {
    //   window.removeEventListener('resize', handleResize);
    // };
  }, []);


  return (
    <div className="App">
      <img className="App-logo" src={Tema} />
      <div className="grid-stack" >

        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content"> <img width="100%" height="100%" src={Tema} /></div>
        </div>

        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
          <div id="chartContainer" style={{ width: '100%', height: '400px' }}>
        <HighchartsReact highcharts={Highcharts} options={getOptions('line')} />
      </div></div>
        </div>
        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
            <HighchartsReact highcharts={Highcharts} options={getOptions('spline')} /></div>
        </div>
        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
            <HighchartsReact highcharts={Highcharts} options={getOptions('area')} /></div>
        </div>
        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
            <HighchartsReact highcharts={Highcharts} options={getOptions('areaspline')} /></div>
        </div>
        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
            <HighchartsReact highcharts={Highcharts} options={getOptions('column')} /></div>
        </div>
        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
            <HighchartsReact highcharts={Highcharts} options={getOptions('bar')} /></div>
        </div>
        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
            <HighchartsReact highcharts={Highcharts} options={getOptions('pie')} /></div>
        </div>
        <div className="grid-stack-item" gs-w="3" gs-h="2">
          <div className="grid-stack-item-content">
            <HighchartsReact highcharts={Highcharts} options={getOptions('scatter')} /></div>
        </div>

      </div>
    </div>
  );
}

export default App;
