import React, { useState } from 'react';
import { Chart } from "react-google-charts";
import './App.css';
import './litegride.css';
import 'gridstack/dist/gridstack.min.css';
import { GridStack } from 'gridstack';
import Tema from './assets/img/tema.jpg';

function App() {
  const [options, setOptions] = useState({
    title: 'Gráfico de Pizza'
  });
  const [optionsBar, setOptionsBar] = useState({
    title: 'Gráfico de Barra'
  });
  const [data, setData] = useState([
    ['Linguagens', 'Quantidade'],
    ['React', 100],
    ['Angula', 80],
    ['Vue', 50],
  ]);
  const [dataBar, setDataBar] = useState([
    ['Cidades', '2010 População', '2000 População'],
    ['New York City, NY', 8175000, 8008000],
    ['Los Angeles, CA', 3792000, 3694000],
    ['Chicago, IL', 2695000, 2896000],
    ['Houston, TX', 2099000, 1953000],
    ['Philadelphia, PA', 1526000, 1517000],
  ]);

  GridStack.init();
  return (
    <div className="App">
      <img className="App-logo" src={Tema}/>
      <></>
      <div className="grid-stack">
        <div className="grid-stack-item">
          <div className="grid-stack-item-content">
            <Chart
              width={'500px'}
              height={'300px'}
              chartType="PieChart"
              data={data}
              options={options}
            />
          </div>
        </div>
        <div className="grid-stack-item" gs-w="2">
          <div className="grid-stack-item-content"><Chart
            width={'500px'}
            height={'300px'}
            chartType="BarChart"
            data={dataBar}
            options={optionsBar}
          /></div>
        </div>
        <div className="grid-stack-item" gs-w="2">
          <div className="grid-stack-item-content"><Chart
            chartType="ScatterChart"
            data={[["Age", "Weight"], [4, 5.5], [8, 12]]}
            width="100%"
            height="400px"
            legendToggle
          /></div>
        </div>
        <div className="grid-stack-item" gs-w="2">
          <div className="grid-stack-item-content"><Chart
            chartEvents={[
              {
                eventName: "select",
                callback: ({ chartWrapper }) => {
                  const chart = chartWrapper.getChart();
                  const selection = chart.getSelection();
                  if (selection.length === 0) return;
                  const region = data[selection[0].row + 1];
                  console.log("Selected : " + region);
                },
              },
            ]}
            chartType="GeoChart"
            width="100%"
            height="400px"
            data={data}
          /></div>
        </div>
        <div className="grid-stack-item" gs-w="2">
          <div className="grid-stack-item-content"><Chart
            chartType="Histogram"
            width="100%"
            height="400px"
            data={data}
            options={options}
          /></div>
        </div>

        <div className="grid-stack-item" gs-w="2">
          <div className="grid-stack-item-content"> <Chart
            chartType="SteppedAreaChart"
            width="100%"
            height="400px"
            data={data}
            options={options}
            legendToggle
          /></div>
        </div>

      </div>
    </div>
  );
}

export default App;
