import React, { useState, useRef, useEffect } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import 'gridstack/dist/gridstack.min.css';
import './App.css';
import './litegride.css';
import { GridStack } from 'gridstack';
import Tema from './assets/img/tema.jpg';
import { useResizeDetector } from 'react-resize-detector';

const GridItem = ({ w = "3", h = "2", children }) => {
  const { width, height, ref } = useResizeDetector();
  return (
    <div className="grid-stack-item" gs-w={w} gs-h={h}>
      <div className="grid-stack-item-content" ref={ref} >
        {children}
      </div>
    </div>
  );
}

function App() {

  const options = {
    chart: {
      type: 'column',
      style: {},
      events: {
        load() {
          setTimeout(this.reflow.bind(this), 0);
        },
      },
    },
    title: 'Column Chart',
  }

  const getOptions = (type) => ({
    chart: {
      type
    },
    responsive: {
      rules: [{
        condition: {
          maxWidth: '100%'
        },
        chartOptions: {
          legend: {
            enabled: false
          }
        }
      }]
    },
    title: {
      text: "Gráfico",
    },
    yAxis: {
      title: {
        text: 'Values',
      },
    },
    series: [
      {
        data: [1, 2, 1, 4, 3, 6],
      },
      {
        data: [2, 7, 0, 4, 6, 2],
      },
    ],
  });

  useEffect(() => {
    GridStack.init();


  }, []);
  // useEffect(() => handleResize(width, height), [width, height])




  return (
    <div className="App" >
      <img className="App-logo" src={Tema} />
      <div className="grid-stack" >
        <GridItem>
          <img width="100%" height="100%" src={Tema} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('line')} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('spline')} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('area')} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('areaspline')} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('column')} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('bar')} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('pie')} />
        </GridItem>
        <GridItem>
          <HighchartsReact highcharts={Highcharts} options={getOptions('scatter')} />
        </GridItem>


      </div>
    </div>
  );
}

export default App;
